% Write matrix with sensor locations and orientations in file in pmg-format (BESA, ASA)% sensors(1:6,1:nr_sensors) => sensors(xyznxnynz,1:nr_sensors)% OH 11.02.98function write_pmg(sensors, filename);if nargin==0,   disp(' write_pmg(sensors, filename); ');   return;end;if isempty(filename),		% No name specified	[name, pathname] = uigetfile('*.pmg', 'Choose a PMG-position file!');	filename = sprintf('%s%s', pathname, name);end;[m n] = size(sensors)fid = fopen(filename, 'w');for i=1:n,   %   fprintf(fid, 'M%d\t\"%d":\t  ', i, i);   fprintf(fid, 'Channel \"A%d\":  ', i);   for j=1:6,      fprintf(fid, '%14.7f   ', sensors(j, i));   end;   fprintf(fid, '\n');end;fclose(fid);