function wroptmp(lfdmat, diploc);G = wrop(lfdmat, diploc, diploc, lfdmat, 3, 0, 1, 0.01);write_matrix(G, 'c:\data\leadfields\wrop65_1.dat');G = wrop(lfdmat, diploc, diploc, lfdmat, 3, 0, 0.1, 0.01);write_matrix(G, 'c:\data\leadfields\wrop65_01.dat');G = wrop(lfdmat, diploc, diploc, lfdmat, 3, 0, 0.01, 0.01);write_matrix(G, 'c:\data\leadfields\wrop65_001.dat');G = wrop(lfdmat, diploc, diploc, lfdmat, 3, 0, 0.001, 0.01);write_matrix(G, 'c:\data\leadfields\wrop65_001.dat');G = wrop(lfdmat, diploc, diploc, lfdmat, 3, 0, 0.0001, 0.01);write_matrix(G, 'c:\data\leadfields\wrop65_0001.dat');