% berechnet Meanpotentials aus ATG.ar konstanz file fuer anzugebende Zeit-% fenstercleartimewins = input (' time window to select and average? (form: [x y])    ')ascii1_name = input ('ascii name for result?      ','s')Nvps = input ('number of subjects in condition?     ')trans = input ('transformiere nach MCCarthy and wood (y/n)?   ','s')for vpindex = 1 : Nvps     % berechne  meanpotentials     [ERPmat] = ReadAvgFile;   % liest ein             % transformiere nach MCCarty and wood        if trans == 'y'        for t = 1 : length (ERPmat);            teilmat = ERPmat(:,t);            minimum = min(teilmat);            maximum = max(teilmat);                        for elek = 1 : 129                neumat(elek, t) = (ERPmat(elek,t)-minimum)/(maximum - minimum);            end        end    end    % schreibe gemittelte werte fuer jede elktrode in superanova-format     ERPneuvec1 = mean(neumat(:,[timewins(1,1):timewins(1,2)])');    ERPsamat (vpindex, [1:129]) = ERPneuvec1;    endeval (['save ubies:Hypnosis_pain:fuer_stats:'ascii1_name ' ERPsamat -ascii'])disp (' ')disp ('saving superanova format ascii file:'), disp (ascii1_name)