% built_safile% ruft fuer gegebene subjects eine funktion auf, die fuer jede% elektrode einen parameter berechnet% SA format vp als zeilen, conds nebeneinander %============================================================% 1. experiment description %===========================================================nrsubjects = input('how many subjects    ?    ');disp(' ')disp(' ')nrelectrodes = input ('how many sensors   ?      ');band = input('freq  band  ?     ');condition = input ('condition ? (ruhe, arith, imag)    ','s')endmatSA = zeros(nrsubjects, nrelectrodes);SuVAmat = zeros(nrsubjects, nrelectrodes);for vp = 1 : nrsubjects						[FILENAME, PATHNAME] = uigetfile ('shamrock:applications:MATLAB:FAPDAT:*');            			FilePath = ([PATHNAME FILENAME]); disp (FilePath), disp (vp)						eval (['load ' FilePath])					  		rawfreqmat = eval(FILENAME);						endmatSA(vp,:) = rawfreqmat(:, band)';						end	nband = num2str(band)fname = ([condition nband])eval(['save ubies:JKtemp:' condition nband ' endmatSA -ascii'])disp ('saving file'), disp(fname)