% waehlt elektroden und/oder Zeitpukte aus t32 sa filefile = input ('name of file     ','s')eval (['load ' file])% Spaltenvektorenbeta = [29:44];ugamma = [45:68];ogamma = [69:90];emg = [91:150];fmat = eval (file);betamat = fmat(:,beta);ugamat = fmat(:,ugamma);ogamat = fmat(:,ogamma);emgmat = fmat(:,emg);band1=mean (betamat');   %19 - 29 hertzband2=mean (ugamat');    %30 -45band3=mean (ogamat');    %46-60band4=mean (emgmat');    %60-100summat = [band1' band2' band3' band4'];eval (['save 'file 'freq  summat -ascii']) 