% app2MNfunction [] = atg2app(atgFilePath);if nargin < 1	[File Path] = uigetfile( '*','select Appfile')	atgFilePath = [Path File];endLHeader = 1000;NChan = 129;% atgfile einlesen	[AvgMat,File,Path,FilePath,NTrialAvgVec,StdChanTimeMat,...	SampRate,AvgRef,Version,MedMedRawVec,MedMedAvgVec] = ...	ReadAvgFile(atgFilePath);	%open new files	%--------------------------------------	% open new file 1 for appdata	%--------------------------------------	NewFileFid1 = fopen([atgFilePath '.app'], 'w')     	fwrite(NewFileFid1,Version,'int16');	fwrite(NewFileFid1,LHeader,'int16');	fwrite(NewFileFid1,200,'int16');	fwrite(NewFileFid1,NChan, 'int16');	fwrite(NewFileFid1,length(AvgMat(1,:)),'int16');	fwrite(NewFileFid1,1,'int16');	fwrite(NewFileFid1,SampRate,'int16');	fwrite(NewFileFid1,1,'int16');	AppTell=ftell(NewFileFid1);	ZeroVec=zeros(LHeader-AppTell,1);	fwrite(NewFileFid1,ZeroVec,'int8');	fwrite(NewFileFid1, AvgMat', 'int16');		endfclose('all')