[NFiles,FileMat]=ReadFileNames([],'*.at*');for FileIndex=1:NFiles		[File,Path,FilePath]=GetFileNameOfMat(FileMat,FileIndex);	[AvgMat,File,Path,FilePath,NTrialAvgVec,StdChanTimeMat,...	SampRate,AvgRef,Version,MedMedRawVec,MedMedAvgVec]=ReadAvgFile(FilePath);	AvgMat=AvgMat.*-1;	StdChanTimeMat=StdChanTimeMat.*1;	MedMedRawVec=MedMedRawVec.*1;	MedMedAvgVec=MedMedAvgVec.*1;	SaveAvgFile(FilePath,AvgMat,NTrialAvgVec,StdChanTimeMat,SampRate,MedMedRawVec,MedMedAvgVec);endreturn;