% readfilenamesAS% READS FILENAMES TO CHAR ARRAYfunction [nrfiles, FileMat, Pathname, NameList] = readfilenamesAS(filemat);NameList = [];count = 1;nrfiles = 0;FileMat = [];while count == 1 		[File,Path]=uigetfile('*', 'select file');			FilePath=[Path,File]		if length (FilePath) == 2, count = 0; return, end	if count == 1		Pathname = Path;	end			FileMat = [FileMat; FilePath]	NameList = [NameList; File]		nrfiles = nrfiles + 1		eval(['cd ' Path])end