%avgchantime%averages channels and timewins%to SA formatchanvec= input('insert names of channels as an integer vector   ')tvec = input('insert indices of timewins to select and average')freqvec = input('insert frequency inidces to select and average  ')vpvec = ['05';'06';'08';'11';'13';'15';'16';'17';'18';'19';'20';'21'];condvec = ['k ';'ok';'sp']for vpInd = 1 : length(vpvec(:,1))   for condInd = 1 : length(condvec(:,1);      for chanInd = 1: length(chanvec)         path = (['F:\collaps\vp' vpvec(vpInd,:) '\'])         File = (['vp' vpvec(vpInd) condvec(condInd) num2str(chanvec(chanInd))])         load([path File])         dummy1 = mean(mean(File(freqvec,tvec)));    end  end   end