%avgchantime%averages channels and timewins%to SA formatchanvec= [2:129];tvec =  input('insert timewin indices to select and average  ')freqvec = input('insert frequency indices to select and average  ')condmeanmat = []vpvec = ['05';'06';'08';'11';'13';'15';'17';'18';'19';'20';'21'];condvec = ['k ';'ok';'sp'] for condInd = 1 : length(condvec(:,1));    allvpmat = []    for vpInd = 1 : length(vpvec(:,1));         dummy1 = []         for chanInd = 1: length(chanvec)         path = (['F:\collaps\vp' vpvec(vpInd,:) '\'])         File = (['vp' vpvec(vpInd,:) deblank(condvec(condInd,:)) num2str(chanvec(chanInd)) 'wpow'])         load([path File])         einzmat = eval(File);         dummy1 = [dummy1 mean(mean(einzmat(freqvec,tvec)))];         eval(['clear ' File])         end         allvpmat = [allvpmat;dummy1];    end    eval(['save C:\users\akeil\' deblank(condvec(condInd,:)) 'allvpmat allvpmat -ascii'])    condmeanmat = [condmeanmat; mean(allvpmat)];end