% bslcorrWAMat_div% DIVIDES the 2dimensional time-frequency matrix selected by the user% from all submatrices(timepints) of 3dim WAmatrixfunction[outWaMat] = bslcorrWAMat_div(inMat, timevec)if ~isempty(timevec)      if nargin < 1   [File, Path] = uigetfile('*');   disp([Path File])   eval(['load ' Path File]);   inMat = AvgWaPower;  end%inMat = permute(inMat, [1 3 2]); bslMat = (mean(inMat(:, timevec,: ),2));outWaMat = inMat ./ repmat(bslMat, [1,size(inMat,2),1]);else    outWaMat = inMat; end    