% GENERDATNEWfunction[WAPower, WARaighley, data]=gd_chapter(jitter);if nargin <1    jitter = []; endrand1 = round(2*rand-1);rand2 = round(2*rand-1);t = [0:0.004:.1]; sig1 = sin(2*pi*t.*30).*2.6;sig2 = sin(2*pi*t.*(50+jitter*rand2)).*1.9; + cos(2*pi*t.*(65+jitter*rand2)).*2.6;s1 = (rand(1,375)-0.5).*.65;s2 = (rand(1,375)-0.5).*.95;[B, A] = butter(5, .24, 'high'); stemp = filtfilt(B, A, s2(226:325)); s3 = [s2(1:225) stemp s2(326:375)]; sigAdd = [zeros(1,145) sig1 zeros(1,58+ jitter * rand1) sig2 zeros(1,120-jitter*rand1)];data = sigAdd + .1* s1 +4 *s3; figure(1)plot(data, 'k');plot(data)figure(2)wavelet = gener_Wav(375, 1, 25, 120);[WAPower, WARaighley]=wa_new(data', wavelet, 375, 1, 25, 120, 375); size (WAPower)faxis = 16.9:0.6757: 81.2; size(faxis)taxis = -500:4:1000-4;size(taxis)contourf(taxis (1:320), faxis, WAPower(:,1:320)), colorbar