% showTFdirfunction [takevec, summat, paramat] = showTFavg(dirname, filemat, taxis, faxis, takevecin);if nargin < 5, takevecin = []; else takevec = takevecin; end%filemat = getfilesindir(dirname)cd(dirname)paramat = [];for index = 1: size(filemat, 1); 	load(filemat(index,:), '-mat');	pause(0.1)    avgmat = outWaMat;	contourf(taxis(10:length(taxis)-5), faxis(6:length(faxis)-15), squeeze(mean(avgmat([65:68 70:73 75:78 83:86 90:92] ,(6:length(faxis)-15), 10:length(taxis)-5))),15), colorbar	if isempty(takevecin), title(filemat(index,:)), else title([filemat(index,:) ' ' num2str(takevec(index))]), end         if index == 1, summat = zeros(size(avgmat)); end     if isempty(takevecin)    flag = input('accept ? [return] (else something else)  ');    if isempty(flag), takevec(index) = 1; disp('accepted'), summat = summat + avgmat;  else takevec(index) = 0; end else pause(1), if takevecin(index), summat = summat + avgmat; paramat = [paramat; squeeze(mean(mean(mean(avgmat(70:90,10:16,33:40))))) squeeze(mean(mean(mean(avgmat(70:90,17:35,70:85))))) ], end  end    endfigurecontourf(taxis(8:length(taxis)-5), faxis(7:length(faxis)-15), squeeze(mean(summat([65:68 70:73 75:78 83:86 90:92],(7:length(faxis)-15), 8:length(taxis)-5))),20), caxis([-0.9 0.28]), colorbartitle('sum of selected')