% wa3d2parabatch% calls wa3d2para for conditionsfunction [paravec] = wa3d2_2_parabatch(directory,elcvec,timevec1,freqvec1,timevec2,freqvec2, outname1, outname2);[filemat, namelist] = getfilesindir(directory)paramat1 = [];paramat2 = [];for vpindex = 1 : size(filemat,1)			file = filemat(vpindex, :);		eval(['load ' file]);		% bslcorrMat = bslcorrwamat(AvgWaPower, [40:109]);		% eval(['save ElNino:users:Kelly:wa_toys_bsl:' namelist(vpindex,:) ' bslcorrMat -mat'])		paramat1(vpindex, :) = wa3d2para_elcs(AvgWaPower, elcvec,timevec1,freqvec1)	paramat2(vpindex, :) = wa3d2para_elcs(AvgWaPower, elcvec,timevec2,freqvec2)		% 					specvec = squeeze((mean(AvgWaPower(50, 17:40, [1:420]), 2)));	% 						% 					plot(specvec, 'r') , title(file), pause	% 						% 					hold on	% 						% 					plot(specvec, 'k') , title(file), pause	% 							%specvec = squeeze(mean(mean(AvgWaPower(:, 17:40, [1:420]), 2), 3));	%size(specvec)	%specmat = [specmat specvec];			end %vpseval(['save elnino:Users:Kelly:4stats:' outname1 ' paramat1 -ascii'])eval(['save elnino:Users:Kelly:4stats:' outname2 ' paramat2 -ascii'])