% wa3d2parabatch% calls wa3d2para for conditionsfunction [paravec] = wa3d2parabatch(directory,elcvec,timevec,freqvec, outname);[filemat, namelist] = getfilesindir(directory)          	for vpindex = 1 : size(filemat,1)                		   file = filemat(vpindex, :);		      		    eval(['load ' file]);			        	 bslcorrMat = bslcorrwamat(AvgWaPower, [40:109]);			 			 eval(['save ElNino:users:Kelly:wa_toys_bsl:' namelist(vpindex,:) ' bslcorrMat -mat'])         			paravec(vpindex) = wa3d2para_elcs(bslcorrMat, elcvec,timevec,freqvec)         	   		% 					specvec = squeeze((mean(AvgWaPower(50, 17:40, [1:420]), 2)));% 					% 					plot(specvec, 'r') , title(file), pause% 					% 					hold on% 					% 					plot(specvec, 'k') , title(file), pause% 													%specvec = squeeze(mean(mean(AvgWaPower(:, 17:40, [1:420]), 2), 3));   					%size(specvec)					%specmat = [specmat specvec];         		      	   			        end %vps               eval(['save elnino:tmpstats:' outname ' paravec -ascii'])               