function[AvgWaPower, SumPower]  = WA_app()% function[AvgWaPower, SumPower]  = WA_app()%% Nur fuer insider%  % f0_start und f_end entsprechen NICHT Frequenzen sondern% Datenpunkten, d.h. wenn die Epoche z.B. 2048 Punkte lang ist% (nach dem Zeropadding) und eine Samplingrate von 500 Hz verwendet wurde% ergibt sich eine Frequenzaufloesung von fa = 500/2048 = 0.244.% fa * fO_start ergibt die tatsaechliche Startfrequenz% fa * f0_end ergibt die tatsaechliche Endfrequenz% Die Wavelets werden nicht fuer jeden Punkt sondern fuer jeden% delta_f0ten Punkt berechnet, um Speicher zu sparen%% Ergibt sich also z.B. ein Wavelet mit 49 Zeilen, f0_start bei 10% und F0_end von 250 und delta_f0 von 5 und 500 Hz ergibt sich:%% 500/2048 = 0.244;% 10 * 0.244  = 2.44 Hz Startfrequenz% 250 * 0.244 = 61 Hz Endfrequenz% 2.44 : 5*0.244 : 61 = 49 Zeilen im Wavelet%% Paramenter sind im Sourcecode einzutragen%% (c) Keil & Gruber, 1999%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%files = ['vp18EX.E1.appg';'vp18OK.E1.appg';'vp18SP.E1.appg';'vp19EX.E1.appg';'vp19OK.E1.appg';'vp19SP.E1.appg';'vp20EX.E1.appg';'vp20OK.E1.appg';'vp20SP.E1.appg';'vp21EX.E1.appg';'vp21OK.E1.appg';'vp21SP.E1.appg']FilePath = ['F:\operant_neu\']disp('electrodes to analyze');electrodes = [1:129]   %Electrodes to analyzedelta_f0 = 10f0_start = 40f0_end = 250%Schleife ueber filesfor nf = 1 : size(files,1)  file_name = [FilePath files(nf,:)]  [data,Version,LHeader,ScaleBins,NChan,NPointsold,NTrials,SampRate,ch_AvgRef]=ReadAppData(file_name);  disp('size of data matrix')  disp(size(data))  % Npoints runden auf naechste 2erpotenz  matsize = 2;  potsize = 2;  while matsize < NPointsold;  potsize = potsize + 1;  matsize = 2^potsize;  end  NPointsNew = matsize;  wavelet = gener_Wav(NPointsNew, delta_f0, f0_start, f0_end);  disp('size of waveletMatrix')  disp(size(wavelet))  disp (' frequency step for delta_f0 = 1 is ')  disp(SampRate/NPointsNew)      % Berechnung der WA  SumPower = [];  AvgWaPower = [];      %schleife ueber KANAELE  for e  = 1 :  size(electrodes,2);     channel = electrodes(e);      % schleife ueber TRIALS	  for trial = 1 : NTrials;         fprintf('wa of channel %g , trial %g of %g   ',channel, trial, NTrials)		 [data]=ReadAppData(file_name, trial, channel);		 % daten padden bis N = NPoints = 2^X       % dazu: daten auf jeden fall als ZEILENVEKTOR !!!!            			a = size(data);               if a (1) ~= 1                  data = data';               end                      data_pad = [data'; zeros(NPointsNew-NPointsold,1)];		 [WAPower, WARayleigh]=wa_new(data_pad, wavelet, NPointsNew, delta_f0, f0_start, f0_end);		 if trial == 1		 SumPower = WAPower;		 else 		 SumPower = SumPower + WAPower;		 end           end     disp('writing channel to Average Matrix')          % !!!!!!!!!!!!!!!! achtung inserted !!!!!!!!!!!!!!!!!!!!     % damit die matrix nicht zu gross wird     % !!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!     disp('resizing matrix')     SumPower = SumPower(:, [1:2:1500]);          AvgWaPower(e,:,:) = SumPower./NTrials;     SumPower = [];   end   disp('saving to disk:   ')   disp([file_name '.wa.mat'])    eval([' save ' [file_name]  '.wa.mat  AvgWaPower -mat'])   AvgWaPower = [];	fclose ('all')	end;	