% wagetparafilemat% reads and averages one value from 3dwapower or RA-file% filemat MUST have order one subject- alle conditions; next subject-all% conditions... etcfunction [paramat] = wagetparafilemat(filemat, dirname, nsubjects, nconditions, elcvec, tvec, fvec);%filemat = getfilesindir(dirname)index = 1; cd(dirname)paramat = [];disp('to do:'),    disp(char(ones(1,nsubjects).*46))    disp('done:')figurefor subj = 1 : nsubjects        fprintf('.')    for condition = 1: nconditions;      	load(filemat(index,:), '-mat');        outwamat = bslcorrWAMat(power(:,:,:), 30:60);    	paramat(subj,condition) = squeeze(mean(mean(mean(outwamat(elcvec,tvec,fvec)))));            contourf(squeeze(mean(outwamat(elcvec,tvec,fvec)))), colorbar, title(filemat(index,:))    pause(.2)    index = index + 1;     endendsound(sin(0:0.2:50*pi),100)