% magno_waveletfunction [AlphaPower] = wavelet_PSB4343c(filemat, f0start, f0end, fdelta, refelec)if nargin < 5,     refelec = []; end% 1. read data to get infofor fileindex = 1:size(filemat,1)  [data,Version,LHeader,ScaleBins,NChan,NPointsold,NTrials,SampRate,ch_AvgRef]=ReadAppData(deblank(filemat(fileindex,:)));  NPointsNew = size(data,2);       % compute wavelets and their parameters   wavelet = gener_wav(NPointsNew, fdelta, f0start, f0end);   disp('size of waveletMatrix')  disp(size(wavelet))  disp (' frequency step for delta_f0 = 1 is ')  disp(SampRate/NPointsNew)       % create 3d matrix objects for wavelet   % channels * time * frequencies       waveletMat3d = repmat(wavelet, [1 1 size(data,1)]);     waveletMat3d = permute(waveletMat3d, [3, 2, 1]);         % loop over trials        disp(['trial index out of '])    disp(NTrials)    disp(['trials:'])        for trialindex = 1:NTrials;                   [data,Version,LHeader,ScaleBins,NChan,NPointsold,NTrials,SampRate,ch_AvgRef]=ReadAppData(deblank(filemat(fileindex,:)), trialindex);                fprintf('.')                 if trialindex/10 == round(trialindex/10), disp(trialindex), end            data = bslcorr(data, 1:100);                 data_pad = data';        % window data with cosine square window                window = cosinwin(50, size(data,2), size(data,1));         data = data .* window;                 data_pad3d = repmat(data_pad', [1 1 size(wavelet,1)]);             % transform data  to the frequency        data_trans = fft(data_pad3d, NPointsNew, 2);        thetaMATLABretrans = [];         ProdMat= waveletMat3d .*(data_trans);        thetaMATLABretrans = ifft(ProdMat, NPointsNew, 2);                % standardize instantaneous phase        stdcomplexphasemat = thetaMATLABretrans ./ abs(thetaMATLABretrans);                % if a reference sensor is given, calculate inter-site PLI for that        % sensor        if ~isempty (refelec)             % size(stdcomplexphasemat)             PLIdiffmat = stdcomplexphasemat- (repmat(stdcomplexphasemat(refelec,:,:), [size(stdcomplexphasemat,1), 1, 1]));                         PLIdiffmat(refelec,:,:) = ones(size( PLIdiffmat(refelec,:,:)));             %standardize the difference            PLIdiffmat = PLIdiffmat./(abs(PLIdiffmat));         end                              if trialindex == 1            WaPowerSum = abs(thetaMATLABretrans).* 10;             PLIsum = stdcomplexphasemat;                 if ~isempty (refelec)            PLIdiffsum = PLIdiffmat;                 end        else        WaPowerSum = WaPowerSum + abs(thetaMATLABretrans).* 10;         PLIsum = PLIsum+ stdcomplexphasemat;            if ~isempty (refelec)               PLIdiffsum = PLIdiffsum+ PLIdiffmat;            end        end    end % loop over trials        %final calculations: avg (and abs for PLI)        WaPower = WaPowerSum./NTrials;      %  PLI = abs(PLIsum./NTrials);     %        if ~isempty (refelec)%          PLIdiff = abs(PLIdiffsum./NTrials); %        end%         eval(['save ' deblank(filemat(fileindex,:)) '.pow3.mat WaPower'])  %  eval(['save ' deblank(filemat(fileindex,:)) '.pli3.mat PLI'])      if ~isempty (refelec),  eval(['save ' deblank(filemat(fileindex,:)) '.ispl3_' num2str(refelec) '.mat PLIdiff']), end            fclose('all');end