function ref = AVG_REF_UGA(filemat);for index = 1:size(filemat,1) [matrix,File,Path,FilePath,NTrialAvgVec,StdChanTimeMat,...	SampRate,AvgRef,Version,MedMedRawVec,MedMedAvgVec,EegMegStatus,...    NChanExtra,TrigPoint,HybridFactor,HybridDataCell,]=ReadAvgFile(deblank(filemat(index,:)));mean_col = mean(matrix', 2);for i=1:length(matrix(1,:)),	ref(:,i) = matrix(:,i)-mean_col(i);end;[File,Path,FilePath]=SaveAvgFile([deblank(filemat(index,:)) '.ar'],ref,NTrialAvgVec,StdChanTimeMat, ...    SampRate,MedMedRawVec,MedMedAvgVec,EegMegStatus,NChanExtra,TrigPoint,HybridFactor,...    HybridDataCell)end