%==================================================================%==================================================================clear all;%%	BatchSnod%%	Markus Junghoefer	[1996]%%%	Function definition%	%%%%%=================================================================%==================================================================TRStatus=1;[NNetFiles,NetFileMat]=ReadFileNames([],'*.RAW','Choose NetStation file:');[ch_UseOverAllGainFile]=IfEmptyInputBo('Do you want to use one special gain and one special zero file ?',[],[],1,1);if ch_UseOverAllGainFile	[GainFile,GainPath,GainFilePath]=ReadFilePath([],'*.Gain','Please choose the over all gain file:');	[ZeroFile,ZeroPath,ZeroFilePath]=ReadFilePath([],'*.Zero','Please choose the over all zero file:');	if GainFile==0; GainFile=[]; GainPath=[]; GainFilePath=[]; end	if ZeroFile==0; ZeroFile=[]; ZeroPath=[]; ZeroFilePath=[]; endelse	GainFilePath=[];	ZeroFilePath=[];end%===============TransNetGeoHistNew====================================%[EGISFileMat,FileMat]=TransNetGeoHistNew(FileMat,TRStatus,PreTrig,PostTrig,ReTrig,GainFilePath,ZeroFilePath,OldZeroVersion,NGainZeroString,SpecCondVec)[EGISFileMat]=TransNetGeoHist_15(NetFileMat,TRStatus,100, 250,1,GainFilePath,ZeroFilePath,0,4);%=============================================================DeleteFileMat(NetFileMat);%===============CalcAutoEditMat ohne avg ref EEG und ohne flat====================================%[AEMFilePathMat]=CalcAutoEditMat(FileMat,TimeStatusVec,ch_CalcAvgRef,ch_UseSpecChan,...%ch_CalcBaseline,BaseStartPoint,BaseEndPoint,ch_ChooseTimeInd,LFlatWin,ch_CalcFlaMat,ch_UseAWE);[AEMFilePathMat]=CalcAutoEditMat(EGISFileMat,ones(1,350),0,0,1,1,100,0,8,0,0);%======Suche nach schlechten Kanaelen ==================================%FindBadChan(AEMFilePathMat,AWELimesFac,BatchStatus);FindBadChan(AEMFilePathMat,3,1);%======Suche nach schlechten trials ==================================% [AWEFileMat]=FindBadChanTrial(FilePathMat,AWELimesFac,BatchStatus);FindBadChanTrial(AEMFilePathMat,2.5,1);%===============CalcAutoEditMat mit avg ref EEG und mit flat====================================[AEMFilePathMat]=CalcAutoEditMat(EGISFileMat,ones(1,350),1,1,1,1,100,0,8,1,1);