% avgmats% average .mat files specified in filematinfunction [outmat] = WAmat2waveform(filematin, freqindices, suffix)%suffix needs to be a string variable (i.e. in ' ' )for fileindex = 1 : size(filematin, 1)		strucmat = load(deblank(filematin(fileindex, :)));         mat = strucmat.WaPower;		disp(filematin(fileindex, :))	outmat = mean(mat(:, :, freqindices), 3); [File,Path,FilePath]=SaveAvgFile([deblank(filematin(fileindex, :)) '.at.' suffix],outmat,[],[], 500);	end