function [KeyMat,FilePath, File]=Readkeydata(FilePath)%==================================================================if nargin<1; FilePath=[]; end[File,Path,FilePath]=ReadFilePath(FilePath,'*','Choose the file:');File;FilePath;Fid=fopen(FilePath,'r');Index = 1;while 1	[String]=fgetl(Fid);	if String == -1, break, end	numvec = str2num(String);	KeyMat(Index) = numvec(4);		Index = Index + 1;	endfclose(Fid);