% hier kommen die filenamen (verzeichnis) function [meanVec, stdVec, psdMat, regcoef] = key2para(dirname); %ohne colon am endefilenames = getfilesindir(dirname)psdMat = zeros(size(filenames, 1), 2049 );for FileIndex = 1 : size(filenames, 1);		fprintf('computing File: %s ', filenames(FileIndex, :))	disp('')		[KeyMat,FilePath,File]=Readkeydata(filenames(FileIndex, :));		KeyMat = KeyMat(301 : length(KeyMat));		% regression 		[KeyMatReg, regkoefs, reggerade] = regression(KeyMat);				% Paras berechnen			meanVec(FileIndex) = mean(KeyMat);	stdVec(FileIndex) = std(KeyMatReg);	    psdMat(FileIndex, :) = (psd(KeyMatReg, 4096, 100))';		regcoef(FileIndex) = regkoefs(2,1);	endeval(['save ' dirname ':meanVector  meanVec -ascii'])eval(['save ' dirname ':stdvector  stdVec -ascii'])eval(['save ' dirname ':psdmatrix  psdMat -ascii'])eval(['save ' dirname ':Regsteigung  regcoef -ascii'])