% atg2avr% reads scads atg and writes 2 besa ascii .avr formatfunction [] = atg2avrMEG(filemat, TSB)	for fileindex = 1 : size(filemat, 1)		[AvgMat,File,Path,FilePath,NTrialAvgVec,StdChanTimeMat,...	SampRate,AvgRef,Version,MedMedRawVec,MedMedAvgVec,EegMegStatus,NChanExtra] = ...	ReadAvgFile(filemat(fileindex,:));					scalemat = AvgMat .* 10^15;			write_avr(scalemat(1:148, :), [filemat(fileindex,:) '.avr'], TSB, 1000/SampRate);				end		