% atg2avr% reads scads atg and writes 2 besa ascii .avr formatfunction [] = atg2values(filemat)	for fileindex = 1 : size(filemat, 1)		[AvgMat,File,Path,FilePath,NTrialAvgVec,StdChanTimeMat,...	SampRate,AvgRef,Version,MedMedRawVec,MedMedAvgVec,EegMegStatus,NChanExtra] = ...	ReadAvgFile(deblank(filemat(fileindex,:)));	    a = AvgMat;	a([75 76 71 72],31) = AvgMat([75 76 71 72],31).*1.2; 	%	a = AvgMat.*-1;    %a = bslcorr(AvgMat, 50:117).*1.2;              SaveAvgFile([deblank(filemat(fileindex,:)) '.f'],a,NTrialAvgVec,StdChanTimeMat, ...    SampRate,MedMedRawVec,MedMedAvgVec,EegMegStatus); 				end		