% avgwinmats% averages SSR-winmats function[avgmat] = avgwinmats(cond, folder);% subjectvec = ['01'; '02'; '04'; '07'; '08'; '09';'10'; '11'; '12';'13';'14';'15';'17'; '18';'19'; '21'; '22'; '24';'25'];%subjectvec = ['201'; '203'];subjectvec = ['02'; '03'; '04'; '05'; '06'; '07';'08'; '09'; '10';'11';'12'];for subject = 1 : size(subjectvec,1)		temp = load([folder 'vp' subjectvec(subject, :) '_winmat_' cond]);		if subject == 1				summat = temp; 	else				summat = summat + temp;	end			endavgmat = summat ./ size(subjectvec,1); disp('saving to disk:')[folder 'GM_' cond '.dat']save([folder 'GM_' cond '.dat'],  'avgmat' ,  '-ascii')		