% bslcorrmat% corrects 129 channel ar mat % by subtracting mean of baseline in samplepointsfunction [data] = bslcorrspec(filemat, bslvec1, bslvec2);for index = 1:size(filemat,1);         atgPath = deblank(filemat(index,:));	    [inmat,File,Path,FilePath,NTrialAvgVec,dummy,SampRate] = ReadAvgFile(atgPath);             for chan = 1 : size(inmat, 1)        data(chan,:) = inmat(chan,:)-mean(inmat(chan,[bslvec1 bslvec2]),2);        end[File,Path,FilePath]=SaveAvgFile([atgPath '.Rspec'],data,NTrialAvgVec,[], SampRate);end