%coheretimefunction [allemat] = coheretime(dirname);% segment fuer long: 2415:3280% taxis = [-200:2.5:7998.5];filemat = getfilesindir(dirname);for file = 1: size(filemat, 1);		data = read_avr(filemat(file, :));		filemat(file, :)		sizedata = size(data);		appendmat = rand(size(data));		datacoh = [data appendmat(:, 1:150)];	for latency = 5:20:3200;				index = 1;		for chan  = 40:42; %visu				%for chan = 1:2:8;  % audi					if chan ~= 41								[Cxy,F] = cohere(datacoh(41,latency:latency+124),datacoh(chan,latency:latency+124),62,400);							index = index+1;			end						cohvec(index) = Cxy(2);				end		 		if latency == 5		cohvec_time = mean(cohvec);		else		cohvec_time =[cohvec_time mean(cohvec)];		end				end		if file == 1		allemat = cohvec_time		else		allemat = [allemat; cohvec_time];		end		end	eval(['save ' dirname 'coheretime.dat   allemat  -ascii']) 				