%copy_file% copies source_file nach target_file% argIn complete Filepaths for source and target% so sorry for umstaendlich...% andreas 2001%...............................................function [] = copy_file(source_file_path,target_file_path);% find root of computer for temporary patha = path; for x = 1:100    if a(x)~= ':'    b(x) = a(x);    else break, return    endendtemppath = [b ':']% find target_file_name (= string after last ':' )for z = 0 : length(target_file_path)	if target_file_path(length(target_file_path)-z)~= ':',		c(z+1) = target_file_path(length(target_file_path)-z);	else break, returnendendtarget_file_name = fliplr(c)% find source_file_name (= string after last ':' )for q = 0 : length(source_file_path)	if source_file_path(length(source_file_path)-q)~= ':',		d(q+1) = source_file_path(length(source_file_path)-q);	else break, returnendendsource_file_name = fliplr(d)%find foldernamessource_folder = source_file_path(1:length(source_file_path)-length(source_file_name))target_folder = target_file_path(1:length(target_file_path)-length(target_file_name)) acopy(source_file_path, temppath); arename([temppath source_file_name] , target_file_name); amove([temppath target_file_name],target_folder);  