% avgwinmats% averages SSR-winmats function [outmat] = getTavgbaby(filemat);for subject = 1 : size(filemat,1)	        s = findstr('.', [filemat(subject,:)]);		matname = [filemat(subject,1:s(1)-1)];		eval(['load ' filemat(subject,:) ' -ascii']); 		eval(['tmpval = X' matname ';']);        outmat = zeros(size(tmpval,1),5); 		tpoints = size(tmpval,2);        fprintf('number of timepoints = %g', tpoints)    fprintf('\r')        divid = floor(tpoints/5);        dividvec = (1:5) * divid;          restveclength = tpoints - divid * 5;         randvec = randperm(5);        for ind = 1: restveclength        dividvec(randvec(ind)) = dividvec(randvec(ind))+1;     end                        for index = 1:5        outmat(:,index) = mean(tmpval(:,dividvec(index)-divid+1:dividvec(index)),2);         end	              save([deblank(filemat(subject,:)) '.5t'],  'outmat' ,  '-ascii')end	