function [Percent_T1, Percent_T2, correct_vec_T2] = get_ERTS_AB_param(filename)fid = fopen(filename)% dummylinesdumline = fgetl(fid);dumline = fgetl(fid);dumline = fgetl(fid);dumline = fgetl(fid);dumline = fgetl(fid);dumline = fgetl(fid);dumline = fgetl(fid);dumline = fgetl(fid);dumline = fgetl(fid);line = 1index = 1			while line > 0				% read data				line = fgets(fid)		if length(line) <5, break, return, end				trialnum = str2num(line(29:31));				%if trialnum ~= index, error('falsch'), end				RTvecKey1(index) = str2num(line(38:44));		RTvecKey2(index) = str2num(line(46:52));				T1_vec(index) = double(line(54));		T2_vec(index) = double(line(61));				trial_art = double(line(76:81))				% infer conditions				if trial_art == double('LAG1-A'), condition_vec(index) = 1;		elseif trial_art == 'LAG1-B', condition_vec(index) = 1;		elseif trial_art == 'LAG2-A', condition_vec(index) = 2;		elseif trial_art == 'LAG2-B', condition_vec(index) = 2;		elseif trial_art == 'LAG4-A', condition_vec(index) = 4;		elseif trial_art == 'LAG4-B', condition_vec(index) = 4;        else condition_vec(index) = 6;		end	            solution_vec_T1 = double('SBHASGEKSDBKGSHBEBFEHZAEBKBBVAARVBBZAVFWSÖMEGMDGSBRGBBWKSKSVFHATDMAGBLBABEMFSDLABKBFSDEWERADBHBBATSDAVDWWBARWSÖESAESVFHBGKAWBRSFBKGSFDEELRSWMZKBHÖBDSBBVSHETMAEBVBBVWKSSEGLAASAFDKABDSGBÖHAWAESMZAAVAEBABTVHEFMBDFBEGBDADSHSEBBRBBBVBSKRGWSDHFWA')        solution_vec_T2 = double('FEUFGKKSBSSBLBEMSUFSBÜBMVMBSBBVSKABLFEAEGABBSBMBBFBVSBSBDURGBLKSNMBEGHBNRMEBLBASLMABMFAEAVHLFMLBBMSBMVGBSFSVEMBLGSVVÜAUDKNBADMSEBÜMVLESFBSSLBUFSVSLHBBAAFBBMASBLSUBBFBMBMVMHGEKBKSLSBVMBREMBMFVABGBBASDBLREEMSVBFMBGGSAVBLMUVBBEKAGSBSGNFSFBULEÜ')		        % infer correc                if condition_vec(index) == 1 & T1_vec(index) == solution_vec_T1(index), correct_vec_T1(index) = 101 % correct T1 lag1        elseif condition_vec(index) == 1 & T1_vec(index) ~= solution_vec_T1(index), correct_vec_T1(index) = 111 % wrong T1 lag1        elseif condition_vec(index) == 2 & T1_vec(index) == solution_vec_T1(index), correct_vec_T1(index) = 102 % correct T1 lag2            elseif condition_vec(index) == 2 & T1_vec(index) ~= solution_vec_T1(index), correct_vec_T1(index) = 112 % wrong T1 lag2            elseif condition_vec(index) == 4 & T1_vec(index) == solution_vec_T1(index), correct_vec_T1(index) = 104 % correct T1 lag4            elseif condition_vec(index) == 4 & T1_vec(index) ~= solution_vec_T1(index), correct_vec_T1(index) = 114 % wrong T1 lag4        elseif condition_vec(index) == 6 & T1_vec(index) == solution_vec_T1(index), correct_vec_T1(index) = 106 % correct T1 lag6            elseif condition_vec(index) == 6 & T1_vec(index) ~= solution_vec_T1(index), correct_vec_T1(index) = 116 % wrong T1 lag6        end		        if condition_vec(index) == 1 & correct_vec_T1(index) == 101 & T2_vec(index) == solution_vec_T2(index), correct_vec_T2(index) = 1 ;% correct T2 lag1        elseif condition_vec(index) == 1 & correct_vec_T1(index) == 101 & T2_vec(index) ~= solution_vec_T2(index), correct_vec_T2(index) = 11 ;% wrong T2 lag1        elseif condition_vec(index) == 2 & correct_vec_T1(index) == 102 & T2_vec(index) == solution_vec_T2(index), correct_vec_T2(index) = 2 ;% correct T2 lag2        elseif condition_vec(index) == 2 & correct_vec_T1(index) == 102 & T2_vec(index) ~= solution_vec_T2(index), correct_vec_T2(index) = 12 ;% wrong T2 lag2        elseif condition_vec(index) == 4 & correct_vec_T1(index) == 104 & T2_vec(index) == solution_vec_T2(index), correct_vec_T2(index) = 4 ;% correct T2 lag4        elseif condition_vec(index) == 4 & correct_vec_T1(index) == 104 & T2_vec(index) ~= solution_vec_T2(index), correct_vec_T2(index) = 14 ;% wrong T2 lag4        elseif condition_vec(index) == 6 & correct_vec_T1(index) == 106 & T2_vec(index) == solution_vec_T2(index), correct_vec_T2(index) = 6 ;% correct T2 lag4        elseif condition_vec(index) == 6 & correct_vec_T1(index) == 106 & T2_vec(index) ~= solution_vec_T2(index), correct_vec_T2(index) = 16 ;% wrong T2 lag4        end            index = index + 1;					endIa=find(correct_vec_T1==101);Percent_T1_lag1=size(Ia,2)/60*100Ib=find(correct_vec_T1==102);Percent_T1_lag2=size(Ib,2)/60*100Ic=find(correct_vec_T1==104);Percent_T1_lag4=size(Ic,2)/60*100Id=find(correct_vec_T1==106);Percent_T1_lag6=size(Id,2)/60*100Ie=find(correct_vec_T2==1);Percent_T2_lag1=size(Ie)/size(Ia)*100If=find(correct_vec_T2==2);Percent_T2_lag2=size(If)/size(Ib)*100Ig=find(correct_vec_T2==4);Percent_T2_lag4=size(Ig)/size(Ic)*100Ih=find(correct_vec_T2==6);Percent_T2_lag6=size(Ih)/size(Id)*100Percent_T1 = [Percent_T1_lag1 Percent_T1_lag2 Percent_T1_lag4 Percent_T1_lag6]Percent_T2 = [Percent_T2_lag1 Percent_T2_lag2 Percent_T2_lag4 Percent_T2_lag6]plot (Percent_T2)ylabel('Prozent Korrekt')xlabel ('lag1   lag2    lag4    lag6')title('T2 report given T1')