function [ERRvec_1bis12, RTvec_1bis12, RTvec1,correctvec,condition_vec] = get_ERTS_bettina(infilepath, tastencode)% tastencode 1 = ALTGR = wort% tastencode 2 = ALTGR = pseudowortfid = fopen(infilepath)% dummylinesdumline = fgetl(fid)dumline = fgetl(fid)dumline = fgetl(fid)dumline = fgetl(fid)dumline = fgetl(fid)dumline = fgetl(fid)dumline = fgetl(fid)dumline = fgetl(fid)dumline = fgetl(fid)%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%  fuer altgr == wortline = 1index = 1if tastencode == 1		while line >= 1				% read data				line = fgets(fid)                		if length(line)==1, break, return, end				trialnum = str2num(line(29:31));	            RTvec1(index) = str2num(line(38:43));		        Key1 = line(46);				trial_art = line(61:67);				% infer conditions				% 1=std_P_50; 2=std_N_50, 3=std_U_50, 4=std_P_250; 5=std_N_250,		% 6=std_U_250 7=std_P_750, 8=std_N_750, 9= std_U_750        % 10=v_P_50; 11=v_N_50, 12=v_U_50, 13=v_P_250; 14=v_N_250,		% 15=v_U_250 16=v_P_750, 17=v_N_750, 18=v_U_750		 		if trial_art == 'POS_P_V' | trial_art == 'POS_N_V' | trial_art == 'POS_U_V', condition_vec(index) = 1;        elseif trial_art == 'NTR_P_V' | trial_art == 'NTR_N_V' | trial_art == 'NTR_U_V', condition_vec(index) = 2;        elseif trial_art == 'NEG_P_V' | trial_art == 'NEG_N_V' | trial_art == 'NEG_U_V', condition_vec(index) = 3;,         elseif trial_art == 'POS_POS', condition_vec(index) = 4; 		elseif trial_art == 'POS_NTR', condition_vec(index) = 5;        elseif trial_art == 'POS_NEG', condition_vec(index) = 6;        elseif trial_art == 'NTR_POS', condition_vec(index) = 7;            elseif trial_art == 'NTR_NTR', condition_vec(index) = 8;        elseif trial_art == 'NTR_NEG', condition_vec(index) = 9;        elseif trial_art == 'NEG_POS', condition_vec(index) = 10;         elseif trial_art == 'NEG_NTR', condition_vec(index) = 11;        elseif trial_art == 'NEG_NEG', condition_vec(index) = 12;        end% 			% infer correct% 				if condition_vec(index) < 4 & Key1 == 'A' , correctvec(index) = 1;        elseif 	condition_vec(index) > 3 & Key1 == 'C', correctvec(index) = 1;        else correctvec(index) = 0;        end		index = index + 1;		endendindex_1 = find(condition_vec == 1 & correctvec == 1); index_2 = find(condition_vec == 2 & correctvec == 1);index_3 = find(condition_vec == 3 & correctvec == 1);index_4 = find(condition_vec == 4 & correctvec == 1)index_5 = find(condition_vec == 5 & correctvec == 1);index_6 = find(condition_vec == 6 & correctvec == 1);index_7 = find(condition_vec == 7 & correctvec == 1);index_8 = find(condition_vec == 8 & correctvec == 1);index_9 = find(condition_vec == 9 & correctvec == 1);index_10 = find(condition_vec == 10 & correctvec == 1);index_11 = find(condition_vec == 11 & correctvec == 1);index_12 = find(condition_vec == 12 & correctvec == 1);mean_1 = mean(RTvec1(index_1));mean_2 = mean(RTvec1(index_2));mean_3 = mean(RTvec1(index_3));mean_4 = mean(RTvec1(index_4));mean_5 = mean(RTvec1(index_5));mean_6 = mean(RTvec1(index_6));mean_7 = mean(RTvec1(index_7));mean_8 = mean(RTvec1(index_8));mean_9 = mean(RTvec1(index_9));mean_10 = mean(RTvec1(index_10));mean_11 = mean(RTvec1(index_11));mean_12 = mean(RTvec1(index_12));RTvec_1bis12 = [mean_1 mean_2 mean_3 mean_4 mean_5 mean_6 mean_7 mean_8 mean_9 mean_10 mean_11 mean_12];% fuer prozent korrekt: corr_1 = length(index_1)./length(find(condition_vec==1)).*100;corr_2 = length(index_2)./length(find(condition_vec==2)).*100;corr_3 = length(index_3)./length(find(condition_vec==3)).*100;corr_4 = length(index_4)./length(find(condition_vec==4)).*100;corr_5 = length(index_5)./length(find(condition_vec==5)).*100;corr_6 = length(index_6)./length(find(condition_vec==6)).*100;corr_7 = length(index_7)./length(find(condition_vec==7)).*100;corr_8 = length(index_8)./length(find(condition_vec==8)).*100;corr_9 = length(index_9)./length(find(condition_vec==9)).*100;corr_10 = length(index_10)./length(find(condition_vec==10)).*100;corr_11 = length(index_11)./length(find(condition_vec==11)).*100;corr_12 = length(index_12)./length(find(condition_vec==12)).*100;ERRvec_1bis12 = [corr_1 corr_2 corr_3 corr_4 corr_5 corr_6 corr_7 corr_8 corr_9 corr_10 corr_11 corr_12];%statmat = [condition_vec' correctvec' RTvec'];eval(['save ' infilepath '.RT RTvec_1bis12 -ascii'])eval(['save ' infilepath '.ER ERRvec_1bis12 -ascii'])bar(RTvec_1bis12)