function [ERRvec_1bis9, RTvec_1bis9, RTvec1, RTvec2,correctvec,condition_vec] = get_ERTS_prp(infilepath, tastencode)% tastencode 1 = ALTGR = wort% tastencode 2 = ALTGR = pseudowortfid = fopen(infilepath)% dummylinesdumline = fgetl(fid)dumline = fgetl(fid)dumline = fgetl(fid)dumline = fgetl(fid)dumline = fgetl(fid)dumline = fgetl(fid)dumline = fgetl(fid)dumline = fgetl(fid)dumline = fgetl(fid)%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%  fuer altgr == wortline = 1index = 1if tastencode == 1		while line >= 1				% read data				line = fgets(fid);        		if line < 0, break, return, end				trialnum = str2num(line(29:31));	            RTvec1(index) = str2num(line(38:43));        		RTvec2(index) = str2num(line(44:51));		        Key1 = line(54);		        Key2 = line(61);				trial_art = line(76:82);				% infer conditions				% 1=std_P_50; 2=std_N_50, 3=std_U_50, 4=std_P_250; 5=std_N_250,		% 6=std_U_250 7=std_P_750, 8=std_N_750, 9= std_U_750        % 10=v_P_50; 11=v_N_50, 12=v_U_50, 13=v_P_250; 14=v_N_250,		% 15=v_U_250 16=v_P_750, 17=v_N_750, 18=v_U_750		 		if trial_art == 'S_POS_5', condition_vec(index) = 1;        elseif trial_art == 'S_NTR_5', condition_vec(index) = 2;        elseif trial_art == 'S_NEG_5', condition_vec(index) = 3; 		elseif trial_art == 'S_POS_2', condition_vec(index) = 4;        elseif trial_art == 'S_NTR_2', condition_vec(index) = 5;        elseif trial_art == 'S_NEG_2', condition_vec(index) = 6;            elseif trial_art == 'S_POS_7', condition_vec(index) = 7;        elseif trial_art == 'S_NTR_7', condition_vec(index) = 8;        elseif trial_art == 'S_NEG_7', condition_vec(index) = 9;         elseif trial_art == 'V_POS_5', condition_vec(index) = 10;        elseif trial_art == 'V_NTR_5', condition_vec(index) = 11;        elseif trial_art == 'V_NEG_5', condition_vec(index) = 12; 		elseif trial_art == 'V_POS_2', condition_vec(index) = 13;        elseif trial_art == 'V_NTR_2', condition_vec(index) = 14;        elseif trial_art == 'V_NEG_2', condition_vec(index) = 15;            elseif trial_art == 'V_POS_7', condition_vec(index) = 16;        elseif trial_art == 'V_NTR_7', condition_vec(index) = 17;        elseif trial_art == 'V_NEG_7', condition_vec(index) = 18;         end% 			% infer correct% 				if condition_vec(index) < 10 & Key2 == 'A' , correctvec(index) = 1;        elseif 	condition_vec(index) > 9 & Key2 == 'C', correctvec(index) = 1;        else correctvec(index) = 0;        end		index = index + 1;		endendindex_1 = find(condition_vec == 1 & correctvec == 1); index_2 = find(condition_vec == 2 & correctvec == 1);index_3 = find(condition_vec == 3 & correctvec == 1);index_4 = find(condition_vec == 4 & correctvec == 1);index_5 = find(condition_vec == 5 & correctvec == 1);index_6 = find(condition_vec == 6 & correctvec == 1);index_7 = find(condition_vec == 7 & correctvec == 1);index_8 = find(condition_vec == 8 & correctvec == 1);index_9 = find(condition_vec == 9 & correctvec == 1);index_10 = find(condition_vec == 10 & correctvec == 1);index_11 = find(condition_vec == 11 & correctvec == 1);index_12 = find(condition_vec == 12 & correctvec == 1);index_13 = find(condition_vec == 13 & correctvec == 1);index_14 = find(condition_vec == 14 & correctvec == 1);index_15 = find(condition_vec == 15 & correctvec == 1);index_16 = find(condition_vec == 16 & correctvec == 1);index_17 = find(condition_vec == 17 & correctvec == 1);index_18 = find(condition_vec == 18 & correctvec == 1);mean_1 = mean(RTvec2(index_1))-50;mean_2 = mean(RTvec2(index_2))-50;mean_3 = mean(RTvec2(index_3))-50;mean_4 = mean(RTvec2(index_4))-250;mean_5 = mean(RTvec2(index_5))-250;mean_6 = mean(RTvec2(index_6))-250;mean_7 = mean(RTvec2(index_7))-750;mean_8 = mean(RTvec2(index_8))-750;mean_9 = mean(RTvec2(index_9))-750;mean_10 = mean(RTvec2(index_10))-50;mean_11 = mean(RTvec2(index_11))-50;mean_12 = mean(RTvec2(index_12))-50;mean_13 = mean(RTvec2(index_13))-250;mean_14 = mean(RTvec2(index_14))-250;mean_15 = mean(RTvec2(index_15))-250;mean_16 = mean(RTvec2(index_16))-750;mean_17 = mean(RTvec2(index_17))-750;mean_18 = mean(RTvec2(index_18))-750;RTvec_1bis9 = [mean_1 mean_2 mean_3 mean_4 mean_5 mean_6 mean_7 mean_8 mean_9];RTvec_10bis18 = [mean_10 mean_11 mean_12 mean_13 mean_14 mean_15 mean_16 mean_17 mean_18];% fuer prozent korrekt: corr_1 = length(index_1)./40.*100;corr_2 = length(index_2)./40.*100;corr_3 = length(index_3)./40.*100;corr_4 = length(index_4)./40.*100;corr_5 = length(index_5)./40.*100;corr_6 = length(index_6)./40.*100;corr_7 = length(index_7)./40.*100;corr_8 = length(index_8)./40.*100;corr_9 = length(index_9)./40.*100;corr_10 = length(index_10)./20.*100;corr_11 = length(index_11)./20.*100;corr_12 = length(index_12)./20.*100;corr_13 = length(index_13)./20.*100;corr_14 = length(index_14)./20.*100;corr_15 = length(index_15)./20.*100;corr_16 = length(index_16)./20.*100;corr_17 = length(index_17)./20.*100;corr_18 = length(index_18)./20.*100;ERRvec_1bis9 = [corr_1 corr_2 corr_3 corr_4 corr_5 corr_6 corr_7 corr_8 corr_9];ERRvec_10bis18 = [corr_10 corr_11 corr_12 corr_13 corr_14 corr_15 corr_16 corr_17 corr_18];%statmat = [condition_vec' correctvec' RTvec'];