function [condvec] = get_pres_log(filepath_6files)line = 1;index = 1;condvec = [];for fileindex = 1:6fid = fopen(filepath_6files(fileindex,:))% dummylinesdumline = fgetl(fid);dumline = fgetl(fid);dumline = fgetl(fid);dumline = fgetl(fid);dumline = fgetl(fid);%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%  			while line > 0								line = fgets(fid)						% segmentiere jede zeile nach tabs				trialnumseg = find(line == '	');				% event-tags		        if line > 0 % read data				eventnum = line(trialnumseg(1)+1:trialnumseg(2)-1);				eventcateg = line(trialnumseg(2)+1:trialnumseg(3)-1);				eventcode = (line(trialnumseg(3)+1:trialnumseg(4)-1))                if eventcode == 'iti'             condvec = condvec        elseif eventcode == '11HL'            condvec(index) = 1; index = index + 1;        elseif eventcode == '12HL'            condvec(index) = 2; index = index + 1;        elseif eventcode == '13HL'            condvec(index) = 3; index = index + 1;        elseif eventcode == '21HL'            condvec(index) = 4; index = index + 1;        elseif eventcode == '22HL'            condvec(index) = 5; index = index + 1;        elseif eventcode == '23HL'            condvec(index) = 6; index = index + 1;        elseif eventcode == '31HL'            condvec(index) = 7; index = index + 1;        elseif eventcode == '32HL'            condvec(index) = 8; index = index + 1;        elseif eventcode == '33HL'            condvec(index) = 9; index = index + 1;                  elseif eventcode == '11LH'            condvec(index) = 11; index = index + 1;        elseif eventcode == '12LH'            condvec(index) = 12; index = index + 1;        elseif eventcode == '13LH'            condvec(index) = 13; index = index + 1;        elseif eventcode == '21LH'            condvec(index) = 14; index = index + 1;        elseif eventcode == '22LH'            condvec(index) = 15; index = index + 1;        elseif eventcode == '23LH'            condvec(index) = 16; index = index + 1;        elseif eventcode == '31LH'            condvec(index) = 17; index = index + 1;        elseif eventcode == '32LH'            condvec(index) = 18; index = index + 1;        elseif eventcode == '33LH'            condvec(index) = 19; index = index + 1;  		        end				% for correctflag    endend	