function [resvec_lat] = getampnew(dirname, indexvec, elecvec)filemat = getfilesindir(dirname);for fileindex = 1 :  size(filemat, 1)		data = read_avr(filemat(fileindex, :));		%[data] = bslcorr(data, 40:80);		filemat(fileindex, :)		%resvec_amp(fileindex) = max(max(data(elecvec, indexvec)));		resvec_lat(fileindex) = find(mean(data(elecvec, indexvec)) == max(mean(data(elecvec, indexvec))));	end