% getcon_ssvepfaces% searches condnumberes in *dat file generarted by ssvepfaces filefunction [content_cond] = getcon_magno_complex(filepath);condvec = []; content_cond = []picvec = [];load complexity.datmediancomp = median(complexity(:,2)); index_low  = find(complexity (:,2) < 3.4616); index_high  = find(complexity (:,2) > 3.4616); lowvec = complexity(index_low,1); highvec = complexity(index_high,1);  fid = fopen(filepath)a = 1;	 while a > 0	a = fgetl(fid);    if a > 0            index1 = findstr(a, '.j');        index2 = findstr(a, ' ');        if isempty (index1); condvec = [condvec 1];        else condvec = [condvec 2];        end        picnum = str2num(a(index2(length(index2))+1:index2(length(index2))+4))                          if  ismember(picnum, lowvec), content_cond = [content_cond  800 + condvec(length(condvec))],         elseif ismember(picnum, highvec), content_cond = [content_cond  900 + condvec(length(condvec))],         end         end     endfclose('all')conditionvec_2 = condvec'; content_cond = content_cond'; eval(['save ' filepath '.con content_cond -ascii'])