% getcon_ssvepfaces% searches condnumberes in *dat file generarted by ssvepfaces filefunction [contentvec1234] = getcon_ssvepfaces(filepath);condvec = []; picvec = [];fid = fopen(filepath)a = 1	 while a > 0	a = fgetl(fid); 	index1 = findstr(a, 'A') 		    if ~isempty (index1);		conletters = deblank(a(index1+4:index1+5));                                 if strcmp(conletters, 'HA'), condvec = [condvec 1];            elseif strcmp(conletters, 'NE'), condvec = [condvec 2];            elseif strcmp(conletters, 'AF'), condvec = [condvec 3];               elseif strcmp(conletters, 'AN'), condvec = [condvec 4];              end        end       endfclose('all')contentvec1234 = condvec'; con4EEG = contentvec1234; eval(['save ' filepath '.con con4EEG -ascii'])