% avgwinmats% averages SSR-winmats function [paravec] = getpara_ascii(filemat, indexvecrows,indexveccolumns, outname, bslvec);for subject = 1 : size(filemat,1)	    string = filemat(subject,:);		eval(['a = load (''' deblank(string) ''');']); 		if ~isempty(bslvec),         tmpval = bslcorr(a, bslvec);     end				paravec(subject) = mean(mean(tmpval(indexvecrows, indexveccolumns))); 		fprintf('.')	endparavec = paravec'; if ~isempty(outname)disp('saving to disk:')disp(outname)save(outname,  'paravec' ,  '-ascii')end	