% avgwinmats% averages SSR-winmats function [paravec] = getpara_mat(filemat, indexvecrows,indexveccolumns);% subjectvec = ['01'; '02'; '04'; '07'; '08'; '09';'10'; '11'; '12';'13';'14';'15';'17'; '18';'19'; '21'; '22'; '24';'25'];%subjectvec = ['201'; '203'];for subject = 1 : size(filemat,1)			eval(['load ' filemat(subject,:) ' -mat']); 					paravec(subject) = mean(mean(ampmat(indexvecrows, indexveccolumns))); 		fprintf('.')		endparavec = paravec'; 