% getcondveceprime% searches picnumbers in e-prime *.txt file% just enter [picvec] = getpicveceprime(filepath);% in command window such as for instance [picvec] = getpicveceprime('ssvep-1-1.txt');% and a column vector picvec will appear, containing all the picnames in% the right order
% this vector will be written to a file with same basename and ending of .pics - good luck!function [picvec, contentvec123] = getparmcarson(picvec);contentvec123 = []; categoryvec = []; picvecfor index = 1:size(picvec,1);     index    if picvec(index) < 1440, contentvec123(index) = 3; categoryvec(index) = 5;     elseif picvec(index) > 1439 & picvec(index) < 1921, contentvec123(index) = 1; categoryvec(index) = 2;    elseif picvec(index) > 1920 & picvec(index) < 3000, contentvec123(index) = 2; categoryvec(index) = 3;    elseif picvec(index) > 2999 & picvec(index) < 3401, contentvec123(index) = 3; categoryvec(index) = 6;    elseif picvec(index) > 4610 & picvec(index) < 4695, contentvec123(index) = 1; categoryvec(index) = 1;    elseif picvec(index) > 4695 & picvec(index) < 7550, contentvec123(index) = 2; categoryvec(index) = 4;     elseif picvec(index) == 7550, contentvec123(index) = 2; categoryvec(index) = 2;  categoryvec(index) = 3;       endend    contentvec123 = contentvec123'categoryvec = categoryvec'outmat_con = [];firstvector = 0:361;firstvector =firstvector';size(firstvector)secondvector = zeros(362,1);secondvector(3:6:362) = 1;thirdvector = zeros(362,1);thirdvector(3:6:362) = contentvec123(1:60);thirdvector(4:6:362) = contentvec123(1:60);thirdvector(5:6:362) = contentvec123(1:60);thirdvector(6:6:362) = contentvec123(1:60);thirdvector(7:6:362) = contentvec123(1:60);thirdvector(8:6:362) = contentvec123(1:60);outmat_con = [firstvector secondvector thirdvector]outmat_cat = [];firstvector = 0:361;firstvector =firstvector';size(firstvector)secondvector = zeros(362,1);secondvector(3:6:362) = 1;thirdvector = zeros(362,1);thirdvector(3:6:362) = categoryvec(1:60);thirdvector(4:6:362) = categoryvec(1:60);thirdvector(5:6:362) = categoryvec(1:60);thirdvector(6:6:362) = categoryvec(1:60);thirdvector(7:6:362) = categoryvec(1:60);thirdvector(8:6:362) = categoryvec(1:60);outmat_cat = [firstvector secondvector thirdvector]outname_con = ['subjectname_run.con.parm']outname_cat = ['subjectname_run.cat.parm']%eval(['save ' outname3 ' outmat -ascii']);dlmwrite(outname_con,outmat_con,'\t')dlmwrite(outname_cat,outmat_cat,'\t')fclose('all')