% gruppiert elektroden und/oder Zeitpukte aus 129 sa file in beliebig viele Gruppen%cleardisp (' !!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!')disp (' !!!!!!!!!!!!! only one time point must be in sa file !!!!!!!!!!')disp (' !!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!')disp (' ')% 1.) variablen:filemat = [];% 2.) filenamenarray erzeugen[nrfiles, filemat, Pathname, NameList] = readfilenamesAS;% 3.) files oeffnen und bearbeitenfor fileindex = 1 : nrfileseval (['load ' filemat(fileindex,:)])vollmat = eval(NameList(fileindex, :));disp('file size:')disp(size(vollmat))disp(' ')disp('file index: ')disp(fileindex)% 2.) inputvariablen  if fileindex == 1	nrvp = input ('number of subjects ?      ')	nrgrups = input ('number of electrode groups ?      ')		suffix = input ('suffix for groupfilename   ?    ','s')	grupmat = zeros (nrvp,nrgrups);			% vectoren einlesen und als vector1 vector2 ... ablegen	 	for c = 1 : nrgrups 		 		elvec = input ('enter vector containing electrode numbers [order: left to right / ant to post] ','s'   ) 	 		eval(['vector'int2str(c) ' = ' elvec]); 	end% 		  end    % beginne eigentlichen Gruppierungsprozess		for elvecindex = 1 : nrgrups				actvec = eval(['vector'int2str(elvecindex)]);		dummymat = vollmat(:,actvec);		a = mean(dummymat')	grupmat(:,elvecindex) = a'    end	grupdatei = ([NameList(fileindex, :) suffix]); 	%[File, Path] = uiputfile('ubies:iapsgam:fuer_stats:*')	disp ('saving file:'), disp (grupdatei)     	eval (['save  elnino:tmpstats:' grupdatei	' grupmat -ascii'])	grupmat = zeros(nrvp,nrgrups);end