%startet invmap: erfragt die Input-Argumente fuer invmaplist und gibt%sie an die funktion invmap_reduce weiter% laedt dazu elek.dat = elcconfig filefunction [] = mnstart(Filemat, namelist, pathname)lade = input ('load and transpose lfmatrix (y,n),      ','s');regpar= input ('regularisierungsparameter?          ');bslvec = input('baselinevector  ? '); if nargin < 1[nrfiles, Filemat, pathname, namelist] = readfilenamesAS;elsenrfiles= size(Filemat, 1);pathname = [pathname]end	if lade == 'y'      disp ('loading leadfield.dat')       load lfd129neu.dat		lfdmat1 = lfd129neu;		clear lfd129neu	endpathout = pwdelplist = ''from = ''to = ''out_flag = 'abs'load elek.datlocations = elek .*10;for fileindex = 1 : size(Filemat,1);     [diploc, lfdmat, data, G, invsol] = invmap_reduce(lfdmat1, regpar, pathname, deblank(namelist(fileindex,:)), pathout, elplist, locations, from, to, out_flag, bslvec);end