%perm_stats_blairfunction [F_vec] = perm_stats_blair(inmat)F_mat = [];for draw =  1:2000; 			for elek = 1:size(inmat,1)		for vp = 1:size(inmat,2)		 			randvec_con = randperm(size(inmat,3));								randmat(elek,vp,:) = inmat(elek,vp,randvec_con);						end	end	[Fcontmat,MScont,MScs, dfcs]=contrast_rep(randmat,[1 -2 1]);		F_sort = sort(Fcontmat);		%F_vec(draw) = F_sort(340);		F_vec(draw) = max(Fcontmat);		if draw/100 == round(draw/100); fprintf('.'), end	endhist(F_vec, 200)