%coheretimefunction [allemat] = powertime(dirname);% segment fuer long: 2415:3280% taxis = [-200:2.5:7998.5];filemat = getfilesindir(dirname);for file = 1: size(filemat, 1);		data = read_avr(filemat(file, :));		filemat(file, :)		sizedata = size(data);		appendmat = rand(size(data));		datacoh = [fliplr(data(:,2450:2800)) data(:,1:2850) data(:,2450:2990) appendmat(:, 1:150)];	for latency = 5:20:3600;				index = 1;		for chan  = 40:42; %visu					if chan == 40			fftmat = fft(datacoh(chan,latency:latency+62));			else			fftmat = fftmat + fft(datacoh(chan,latency:latency+62));			end									fftmat = fftmat ./3;			NFFT = 62;			NumUniquePts = ceil((NFFT+1)/2); 														Mag = abs(fftmat);                                                   % Amplitude berechnen			Mag = Mag*2;   						Mag=Mag/NFFT;                                                         % FFT so skalieren, da§ sie keine Funktion von NFFT ist											powervec(index) = Mag(2);			index = index +1;				end		 		if latency == 5		powervec_time = mean(powervec);		else		powervec_time =[powervec_time mean(powervec)];		end				end		if file == 1		allemat = powervec_time		else		allemat = [allemat;  powervec_time];		end		end	eval(['save ' dirname 'powertime20vor.dat   allemat  -ascii']) 				