% function [c] = proportionssVEP(filemat1, filemat2);for fileInd = 1:size(filemat1,1);       [a,File,Path,FilePath,NTrialAvgVec,StdChanTimeMat,...	SampRate,AvgRef,Version,MedMedRawVec,MedMedAvgVec,EegMegStatus,...    NChanExtra,TrigPoint,HybridFactor,HybridDataCell,DataTypeVal] = ReadAvgFile(deblank(filemat1(fileInd,:)));   a = a.*2; b = ReadAvgFile(deblank(filemat2(fileInd,:)));      c = (a-b) .* (a +b) ./ 2;         outname = [deblank(filemat1(fileInd,:)) '.prop']SaveAvgFile(outname,c ,NTrialAvgVec,StdChanTimeMat, ...    SampRate,MedMedRawVec,MedMedAvgVec,EegMegStatus,NChanExtra,TrigPoint,HybridFactor);   end        