%coheretimefunction [allemat] = coheretime(dirname);% segment fuer long: 2415:3280% taxis = [-200:2.5:7998.5];filemat = getfilesindir(dirname);for file = 1: size(filemat, 1);			data = read_avr(filemat(file, :));		filemat(file, :)		sizedata = size(data);		appendmat = zeros(size(data));		datacoh = [data appendmat(:, 1:150)];	for latency = 80:50:3200;				index = 1;		for chan  = 36:2:44; 			Cxy = cohere(datacoh(41,latency:latency+150),datacoh(chan,latency:latency+150),50,400);				index = index+1;				cohvec(index) = Cxy(2); 				end		 		if latency == 80		cohvec_time = mean(cohvec);		else		cohvec_time =[cohvec_time mean(cohvec)];		end				end		if file == 1		allemat = cohvec_time		else		allemat = [allemat; cohvec_time];		end		end	eval(['save ' dirname 'coheretime.dat   allemat  -ascii']) 						