function [] = tempavgavr(dirname)filemat = getfilesindir(dirname);for fileindex = 1 :  size(filemat, 1)-1		data = read_avr(filemat(fileindex, :));	filemat(fileindex, :)		 size(data, 1)				if size(data,1) ~= 64;		datanew = data(1:64,:);		disp('writing')		 write_avr(datanew, [filemat(fileindex,:) '.new'], -200, 2.5)		 		else		datanew = data;			end				 			end