[filename, pathname] = uigetfile('S:\','Name der Datenmatrix ?');file = sprintf('%s%s',pathname,filename);eval(['load ' file]);new = strrep(filename,'.txt','');s1 = sprintf('DATA = %s;',new);eval(s1);vp = input ('Wieviel Versuchspersonen gibt es ?');gr = size(DATA);zeilen = gr(1);spalten = gr(2);step = vp*5;z=0;a=0;for j = 1:vp	for i = 5:spalten      for x = 1:5         NEUDATA(j,(x+z)) = DATA(x+a,i);      end;      z=z+5;   end;   z=0;a=a+5;end;neudata = runde(NEUDATA,1000);s = strcat('repeat_', filename);w = strcat(pathname,s)f = sprintf('save %s neudata -ascii',w);eval(f);            