% Ttest.m erhaelt zwei vektoren%	berechnet T-wert und gibt ihn und a>b greatFlag = 1 oder b>a greatFlag = -1 ausfunction[tVal, greatFlag] = Ttestak(vector1, vector2)% 1. berechne ML-schaetzer fuer standardfehler der differenzen   SigmaDach = sqrt((sum((vector1-mean(vector1)).^2) + sum((vector2-mean(vector2)).^2)) / (length(vector1) + length(vector2))) * (sqrt((1/length(vector1))+ (1/length(vector2))));         % 2. berechne T-wert fuer unabh. Stichproben und ungl NtVal = (mean(vector1)- mean(vector2))/SigmaDach ;% 3. bestimme greatFlagif mean(vector1)> mean(vector2)   greatFlag = 1;elseif mean(vector2)> mean(vector1)   greatFlag = -1;else   greatFlag = 0;end