% lfdmat1: Leadfield-Matrix fuer Sensor- und Dipolkonfiguration% regpar: Regularisierungsparameter ("je groesser, desto glatter", Erfahrungswert (64 Elektroden): 0.001 (aber Vorsicht!)% pathname: Pfadname fuer *.avr-Datei% namelist: Liste mit *.avr-Dateien (mit Endung!), z.B.: namelist = char('dat1.avr', 'dat2.avr')% pathout: Ausgabepfad, Dateinamen bestehen aus ersten drei Buchstaben der namelist-Namen, +mn[1,2,3...] (oder mnrad[1,2,3...])% elplist: Auszuschliessende Elektroden, z.B. elplist = [44, 61], oder elplist=''% from, to: Zu bearbeitende Spalten der Datenmatrix, falls alle bearbeitet werden sollen: '', ''% out_flag: 'abs'=>Betraege der Dipolmomente (>0), 'rad'=>Radialteile der Dipolmomentefunction [diploc, lfdmat, data, G, invsol] = invmap(lfdmat1, regpar, pathname, namelist, pathout, elplist, from, to, out_flag);if nargin==0,   disp(' [diploc, lfdmat, data, G, invsol] = invmap(lfdmat1, regpar, pathname, namelist, pathout, elplist, from, to, out_flag); ');   return;end;isfrom = 1;if isempty(from),   isfrom=0;end;nr_names = length(namelist(:,1)) disp('Reading in data, average referencing');name = sprintf('%s\\%s', pathname, namelist(1,:));disp(name);[matrix, latencies] = read_avr(name);[m(1) n(1)] = size(matrix);disp(' Determine valid electrodes to be included in the following procedure');nr_electrodes = 0;for i=1:m(1),	pruef = 0;	for j=1:length(elplist),		if elplist(j)==i,			pruef = 1;			break;		end;	end;	if pruef==0,		nr_electrodes = nr_electrodes + 1;		arg(nr_electrodes) = i;	end;end;nr_electrodesif isfrom==0, from=1; to=n(1); end;n(1) = to-from+1;data(:,1:n(1), 1) =  matrix(arg,from:to);data(:,1:n(1), 1) = avg_ref(data(:,1:n(1), 1));m(1)for i=2:nr_names,				% Reading in additional files	name = sprintf('%s\\%s', pathname, namelist(i,:));	disp(name);   [matrix, latencies] = read_avr(name);   [m(i) n(i)] = size(matrix);    if isfrom==0, from=1; to=n(i); end;   n(i) = to-from+1;   data(:,1:n(i), i) =  matrix(arg,from:to);	data(:,1:n(i), i) = avg_ref(data(:,1:n(i), i));	m(i)end;disp('Reading dipole locations (diploc)');diploc = read_matrix(1384, 3, 'c:\users\andreas\leadfield\diploc_sph_08_02_01_-06.dat');diploc = diploc';if isempty(lfdmat1),	disp('Reading leadfield matrix (lfdmat)');	lfdmat = read_matrix(4152, 21, 'c:\Matlab\analysis\elp\lead21_pol_08_02_01_-06.dat');	lfdmat = lfdmat';	size(lfdmat)end;if nr_electrodes~=length(lfdmat1(:,1)),   disp(' Resizing and reaveragereferencing leadfield matrix ');   lfdmat = lfdmat1(arg,:);   lfdmat = avg_ref(lfdmat);else   lfdmat = lfdmat1;end;    disp('Computing pseudoinverse (G)')G = pinv_tikh(lfdmat, regpar);size(G)[q, r] = when_changes_radius(diploc(1:3,:), 0.001)disp('Computing and writing  inverse solutions (invsol)')nr_nameslaengeq=length(q)   dim = 3;	for i=1:nr_names,               inv =  inv_recon(G, data(1:nr_electrodes,1:n(i),i), dim);		% Modulo (Intensity map)                        for j=1:length(q),             filename = sprintf('%s\\%smn%d.avr', pathout, namelist(i,1:3), j);         	 disp(filename);	          fid = fopen(filename, 'w')	      if j==1,            invsol(:,1:n(i),i) = inv;            j            i            write_avr(inv(1:q(1),1:n(i)), filename, 1, 1);	      elseif j==length(q),	         invsol(:,1:n(i),i) = inv;	         write_avr(inv(q(j-1)+1:length(diploc(1,:)), 1:n(i)), filename, 1, 1);	      else	         invsol(:,1:n(i),i) = inv;	         write_avr(inv(q(j-1)+1:q(j), 1:n(i)), filename, 1, 1);         end;		end;			% j   end;		% i      